mapper.insert:
/* 회원저장 SQL */
INSERT INTO MEMBER(
	MEMBER_ID
	, NICKNAME
	, PASSWORD
	, NAME
	, EMAIL
	, GENDER
	, TEL
	, M_IMG
	, M_COPY_IMG
	, SNS_TYPE
	, SNS_ID
	, GRADE
) VALUES(
	#{memberId}
	, #{nickname}
	, #{password}
	, #{name}
	, #{email}
	, #{gender}
	, #{tel}
	, ''
	, ''
	, #{snsType}
	, #{snsId}
	, '1111'
)

mapper.select:
		/* 아이디 중복체크 SQL */
		SELECT 
			COUNT(MEMBER_ID) AS CNT
		FROM MEMBER
		WHERE 1=1

        	AND MEMBER_ID = #{memberId}


        	AND NAME = #{name}

/* 비밀번호 찾기 */
SELECT
MEMBER_ID
, PASSWORD
, NAME
, EMAIL
, TEL
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND   EMAIL = #{email}

mapper.update:
/* 비밀번호 변경 */
UPDATE
MEMBER
SET
PASSWORD = #{password}
WHERE MEMBER_ID = #{memberId} AND EMAIL = #{email}
